function Download() {
  const [versionInfo, setVersionInfo] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch('https://midlight.ai/releases/tauri-latest.json')
      .then(res => res.json())
      .then(data => {
        setVersionInfo(data);
        setLoading(false);
      })
      .catch(err => {
        console.error('Failed to fetch version info:', err);
        setLoading(false);
      });
  }, []);

  const version = versionInfo?.version || '0.1.0';
  const macDownload = `https://midlight.ai/releases/midlight-next-${version}-macos.dmg`;
  const winDownload = `https://midlight.ai/releases/midlight-next-${version}-windows.msi`;
  const linuxDownload = `https://midlight.ai/releases/midlight-next-${version}-linux.AppImage`;

  return (
    <div className="app">
      <Header />

      {/* Download Hero */}
      <section className="download-hero">
        <div className="container">
          <h1>Download Midlight</h1>
          <p className="download-subtitle">
            Choose your platform and start writing distraction-free.
          </p>

          <div className="download-cards">
            {/* macOS Card */}
            <div className="download-card">
              <div className="platform-icon">
                <AppleIcon />
              </div>
              <h2>macOS</h2>
              <p className="version">Version {loading ? '...' : version}</p>
              <a
                href={macDownload}
                className="btn-primary download-btn"
              >
                Download for Mac
              </a>
              <div className="system-requirements">
                <h4>System Requirements</h4>
                <ul>
                  <li>macOS 10.15 (Catalina) or later</li>
                  <li>Apple Silicon or Intel processor</li>
                  <li>4 GB RAM minimum</li>
                  <li>200 MB available storage</li>
                </ul>
              </div>
            </div>

            {/* Windows Card */}
            <div className="download-card">
              <div className="platform-icon">
                <WindowsIcon />
              </div>
              <h2>Windows</h2>
              <p className="version">Version {loading ? '...' : version}</p>
              <a
                href={winDownload}
                className="btn-primary download-btn"
              >
                Download for Windows
              </a>
              <div className="system-requirements">
                <h4>System Requirements</h4>
                <ul>
                  <li>Windows 10 (64-bit) or later</li>
                  <li>x64 processor</li>
                  <li>4 GB RAM minimum</li>
                  <li>200 MB available storage</li>
                </ul>
              </div>
            </div>

            {/* Linux Card */}
            <div className="download-card">
              <div className="platform-icon">
                <LinuxIcon />
              </div>
              <h2>Linux</h2>
              <p className="version">Version {loading ? '...' : version}</p>
              <a
                href={linuxDownload}
                className="btn-primary download-btn"
              >
                Download for Linux
              </a>
              <div className="system-requirements">
                <h4>System Requirements</h4>
                <ul>
                  <li>Ubuntu 20.04+ or equivalent</li>
                  <li>x64 processor</li>
                  <li>4 GB RAM minimum</li>
                  <li>200 MB available storage</li>
                </ul>
              </div>
            </div>
          </div>
        </div>
      </section>

      <Footer />
    </div>
  );
}

export default Download;
